import { createSlice, createAsyncThunk } from '@reduxjs/toolkit';

const BASE_URL = 'http://3.6.88.125/pickupinstoreapi/index.php/api/v1/';

export const fetchdepartment = createAsyncThunk('home/departments', async () => {
  try {
    const response = await fetch(`${BASE_URL}departments`);
    if (!response.ok) {
      throw new Error(`HTTP error! status: ${response.status}`);
    }
    const json = await response.json();
    return json;
  } catch (error) {
    console.error('Error fetching departments:', error);
    throw error;
  }
});

export const departmentSlice = createSlice({
  name: 'department',
  initialState: {
    isLoading: false,
    isError: false,
    departments: [],
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchdepartment.pending, (state) => {
        state.isLoading = true;
        state.isError = false;
        state.error = null;
      })
      .addCase(fetchdepartment.fulfilled, (state, action) => {
        console.log('Full payload:', action.payload);
        state.departments = action.payload.data.departments;
        state.isLoading = false;
        state.isError = false;
        state.error = null;
      })
      .addCase(fetchdepartment.rejected, (state, action) => {
        console.error('Fetch departments failed:', action.error.message);
        state.isLoading = false;
        state.isError = true;
        state.error = action.error.message;
      });
  },
});

export default departmentSlice.reducer;
