    const dispatch = useDispatch();
    const city = useSelector(state => state.UserPreference.city)
  
  
    let {selectedCity,selectedpincode,cityList}=useSelector(state=>state.city);
const {mostviewedstores}=useSelector(state=>state.shop);
const {mostviewedproducts}=useSelector(state=>state.product);

    useEffect(() => {
      
     
        dispatch(getmostviewed({"city":selectedCity}));
        dispatch(fetchmostviewed({"city":selectedCity,"pincode":"0"}))

    }, [selectedCity,selectedpincode])

