import { Link } from "react-router-dom";
import Slider from "react-slick";
import { useDispatch, useSelector } from 'react-redux';
import { fetchdepartment } from "../../Redux/Slice/DepartmentSlice";

import'./Homeslider.css';

import React, { useState, useEffect, Suspense,useCallback } from 'react';

const Homeslider = () => {
    const settings = {
        dots: false,
        infinite: false,
        speed: 500,
        slidesToShow: 8,
        slidesToScroll: 8,
        initialSlide: 0,
        responsive: [
          {
            breakpoint: 1024,
            settings: {
              slidesToShow: 6,
              slidesToScroll: 6,
              infinite: true,
              dots: false
            }
          },
          {
            breakpoint: 800,
            settings: {
              slidesToShow: 5,
              slidesToScroll: 5,
              infinite: true,
              dots: false
            }
          },
          {
            breakpoint: 600,
            settings: {
              slidesToShow: 4,
              slidesToScroll: 4,
              initialSlide: 4
            }
          },
          {
            breakpoint: 480,
            settings: {
              slidesToShow: 3,
              slidesToScroll: 3
            }
          }
        ]
      };
      const dispatch = useDispatch();

      const { departments } = useSelector(state => state.department);
console.log(departments);
  useEffect(() => {
    dispatch(fetchdepartment());
  }, [dispatch]);
    return(
        <>
        <Suspense>
        <div className="container-fluid homecategoryimage"> 
            <div className="row">
        <p>home slider</p>
        </div>
            </div>
         
        </Suspense>
        </>
    )
} 

export default Homeslider;
