import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
const BASE_URL = 'http://13.201.130.241/reactapi/public/v1/';
export const getSearch = createAsyncThunk(
  'search/getSearch',
  async ({  selectedCity,term }) => {
   try {
console.log(selectedCity);
       const response = await fetch(`${BASE_URL}Search/autocomplete`, {
           method: 'POST',
           headers: {
             'Content-Type': 'application/json',
           },
           body: JSON.stringify({
                    term: term,
                    city: selectedCity,
                    departmentId: 'All',
                  })
         });
         if (!response.ok) {
           throw new Error('Failed to send OTP');
         }

    const data = await response.json();

    return data;
      } catch (error) {
        // Handle error
        throw error;
      }
  }

);

const searchSlice = createSlice({
  name: 'search',
  initialState: {
    stores: [],
    brands: [],
    loading: false,
  },
  reducers: {
    // ...
  },
  extraReducers: (builder) => {
    builder
      .addCase(getSearch.pending, (state) => {
        state.loading = true;
      })
      .addCase(getSearch.fulfilled, (state, action) => {

        state.loading = false;
        state.stores = action.payload.data.stores;
        state.brands = action.payload.data.brands;
      })
      .addCase(getSearch.rejected, (state) => {
        state.loading = false;
        // Handle the error state if needed
      });
  },
});

export default searchSlice.reducer;
