import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { createApi, fetchBaseQuery } from '@reduxjs/toolkit/query/react';

import BASE_URL from '../../Config/apii';

export const mobileLogin = createAsyncThunk(
  'Useraccount/mobileLogin',
 async (body, { rejectWithValue }) => {
    try {
      console.log("dsds",body);
      const response = await fetch(`${BASE_URL}Useraccount/logindetail`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(body),
      });
      if (!response.ok) {
        throw new Error('Failed to send OTP');
      }

      const data = await response.json();

      return data;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);
export const sendOtp = createAsyncThunk(
  'user/sendOtp',
  async (body, { rejectWithValue }) => {
    try {
      const response = await fetch(`${BASE_URL}Useraccount/sendotp`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(body),
      });

      if (!response.ok) {
        throw new Error('Failed to send OTP');
      }

      const data = await response.json();
      return {
        mobile: body.number,
        otp: body.otp,
      };
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);



const userSlice = createSlice({
  name: 'user',
  initialState: {
    user: [],
    otp:"1111",
    mobile:"",
    isLoading: false,
    error: null,
    UserId:"",
  },
  reducers: {
  setUser: (state, action) => {
        // Update the state with the new user data
        state.user = action.payload.data;
        state.UserId = action.payload.data.UserId;
      },
  logoutUser: (state) => {
        // Clear the user data in the state
        state.user = [];
        state.otp = "";
        state.mobile = "";
        state.UserId = "";
      },},
  extraReducers: (builder) => {
    builder
      .addCase(mobileLogin.pending, (state) => {
        state.isLoading = true;
        state.error = null;
      })
      .addCase(mobileLogin.fulfilled, (state, action) => {
        state.user = action.payload.data;
        state.UserId=action.payload.data.UserId;
        state.isLoading = false;
        state.error = null;
      })
      .addCase(mobileLogin.rejected, (state, action) => {
        state.isLoading = false;
        state.error = action.payload;
      })
      .addCase(sendOtp.fulfilled, (state, action) => {
        state.mobile = action.payload.mobile;
        state.otp = action.payload.otp;
        state.isLoading = false;
        state.error = action.payload;
      });
  },
});


export default userSlice.reducer;
