import { createSlice,createAsyncThunk } from '@reduxjs/toolkit';

const BASE_URL = 'http://3.109.102.154/reactapi/index.php/';

export const fetchdepartment = createAsyncThunk('home/department/', async () => {
    try {
  
      const response1 = await fetch(`${BASE_URL}departments`);
      const json1 = await response1.json();
  
      return json1;
    } catch (error) {
      // Handle error
      throw error;
    }
  });



export const departmentSlice = createSlice({
  name: 'department',
  initialState: {
    isLoading: false,
    isError: false,
    departments: [],
    error: null,
  },
  reducers: {},
 
  extraReducers: (builder) => {
    builder.addCase(fetchdepartment.pending, (state) => {
      state.isLoading = true;
      state.error = null;
    });
    builder.addCase(fetchdepartment.fulfilled, (state, action) => {
      state.departments = action.payload.data;
      state.isLoading = false;
      state.error = null;
    });
    builder.addCase(fetchdepartment.rejected, (state, action) => {
      state.isLoading = false;
      state.error = action.error.message;
    });
  },
});

export default departmentSlice.reducer;
